async function addMovieHandler(movie) {
  try {
    const response = await fetch('https://react-practice-86a6e-default-rtdb.firebaseio.com/movies.json', {
      method: 'POST', // Specify the method as a string
      body: JSON.stringify(movie),
      headers: {
        'Content-Type': 'application/json', // Specify the content type
      },
    });

    if (!response.ok) {
      throw new Error('Failed to add movie'); // Handle HTTP error
    }

    const data = await response.json();
    console.log(data);
  } catch (error) {
    console.error('Error adding movie:', error.message);
    // Handle the error here, e.g., set an error state or show a message to the user
  }
}
