import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import React, { useState } from "react";
function Expenses(props) {
  const [selectedValue, setSelectedValue] = useState("2019, 2021 & 2022");
 const [filterInfoText,setFilterInfoText] = useState()
  const yearSelectedHandler =(event)=>{
      
      setSelectedValue(event);
  }
  return (
    <div>
      
    <Card className="expenses"> 
    <ExpensesFilter selected={selectedValue} onYear = {yearSelectedHandler} ></ExpensesFilter>
    <p>Data for 2 is hidden</p>
      <ExpenseItem expense={props.expense[0]}></ExpenseItem>
      <ExpenseItem expense={props.expense[1]}></ExpenseItem>
      <ExpenseItem expense={props.expense[2]}></ExpenseItem>
      <ExpenseItem expense={props.expense[3]}></ExpenseItem>
    </Card>
    </div>
  );
}
export default Expenses;
