import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import React, { useState } from "react";
function Expenses(props) {
  const [selectedValue, setSelectedValue] = useState('2020');
let filterInfoText = '2019,2021 & 2022';
if(selectedValue === '2019')
{
  filterInfoText = '2020,2021 & 2022';
}
else if(selectedValue === '2020'){
  filterInfoText = '2019,2021 & 2022';
}
else if(selectedValue === '2021'){
  filterInfoText = '2019,2020 & 2022';
}
else{
  filterInfoText = '2019,2020 & 2021';
}
  const yearSelectedHandler =(val)=>{
      setSelectedValue(val);
     
      
  }
  return (
    <div>
      
    <Card className="expenses"> 
    <ExpensesFilter selected={selectedValue} onYear = {yearSelectedHandler} ></ExpensesFilter>
    <p>Data for {filterInfoText} is hidden</p>
    {props.items.map(expense )}
      <ExpenseItem expense={props.expense[0]}></ExpenseItem>
      <ExpenseItem expense={props.expense[1]}></ExpenseItem>
      <ExpenseItem expense={props.expense[2]}></ExpenseItem>
      <ExpenseItem expense={props.expense[3]}></ExpenseItem>
    </Card>
    </div>
  );
}
export default Expenses;
