import { useState } from 'react';
import useFirebaseTasks from '../../hooks/use-dao';
import Section from '../UI/Section';
import TaskForm from './TaskForm';

const NewTask = (props) => {
  const { addTask ,isLoading,} = useFirebaseTasks(); // Use the hook to get addTask

  const [taskText, setTaskText] = useState('');



  const handleAddTask = async () => {
    if (taskText.trim() === '') {
      return; // Don't add an empty task
    }

    try {
      await addTask(taskText); // Use the addTask function from the hook
      onEnterTask(); // Notify the parent component about task entry
      setTaskText(''); // Clear the input field
    } catch (error) {
      // Handle any errors from adding the task
      console.error('Error adding task:', error);
    }
  };

  return (
    <Section>
      <TaskForm onEnterTask={handleAddTask} loading={isLoading} />
      {error && <p>{error}</p>}
    </Section>
  );
};

export default NewTask;
