import React from "react";

function Result(props){
    return(<>
        {/* Show the table with results if yearlyData is available */}
        {props.yearlyData.length === 0 && <p>No results found</p>}
        {props.yearlyData.length > 0 && (
          <table className="result">
            <thead>
              <tr>
                <th>Year</th>
                <th>Total Savings</th>
                <th>Interest (Year)</th>
                <th>Yearly Savings</th>
              </tr>
            </thead>
            <tbody>
              {props.yearlyData.map((data) => (
                <tr key={data.year}>
                  <td>{data.year}</td>
                  <td>{data.savingsEndOfYear}</td>
                  <td>{data.yearlyInterest}</td>
                  <td>{data.yearlySavings}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}</>);
}

export default Result;
