import React, { useState } from "react";


function AddUser() {
  const [usersDetails, setUsersDetails] = useState([{
    userName:"",
    age:"",
  }]);
  const handleChange = (event) => {
    const { name, value } = event.target;
    setUsersDetails((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

 

  const handleAddUser = () => {
    // Add your logic to handle adding the user here
    console.log("Username:", usersDetails.userName);
    console.log("Age:", usersDetails.age);
    // You can add the logic to store the user data or send it to a server here
  };

  return (
    <div>
      <form>
        <div >
          <label >Username:</label>
          <input type="text" value={usersDetails.userName} onChange={handleChange} />
        </div>
        <div>
          <label >Age:</label>
          <input  type="number" value={usersDetails.age} onChange={handleChange} />
        </div>
        <button type="button" onClick={handleAddUser}>
          Add User
        </button>
      </form>
    </div>
  );
}
export default AddUser;
