import React, { useState } from "react";
import Button from '../Button/Button';
import Card from '../Card/Card'
import classes from './AddUser.module.css';
import ErrorModal from '../ErrorModal/ErrorModal';
function AddUser(props) {
  const [usersDetails, setUsersDetails] = useState({
    userName: "",
    age: "",
  });
  const [showPopup, setShowPopup] = useState(false);
 const [error,setError] =useState('Fill the details ...cant be empty');
  const handleClosePopup = () => {
    setShowPopup(false);
  };
  const handleChange = (event) => {
    const { name, value } = event.target;
   
    setUsersDetails((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  
  };

  const handleAddUser = (event) => {
    event.preventDefault();
    console.log("Username:", usersDetails.userName);
    console.log("Age:", usersDetails.age);
    // You can add the logic to store the user data or send it to a server here
    if((usersDetails.userName!=='' && usersDetails.age!=='')&& usersDetails.age>1){
    props.users(usersDetails);
    
    }
    else{
       setShowPopup(true);
       if(!(usersDetails.age)){
        setError('Cant be empty');
       }
       else{
        setError('age should be greater than 1');
        
       }
       
    }
    setUsersDetails({
      userName: "",
      age: "",
    });
  };

  return (
    [
    <Card className ={classes.input} key=''>
      <form >
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="userName" // Set the name attribute to identify the property in state
            value={usersDetails.userName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Age:</label>
          <input
            type="number"
            name="age" // Set the name attribute to identify the property in state
            value={usersDetails.age}
            onChange={handleChange}
            required
          />
        </div>
      
        <Button type="submit" onClick={handleAddUser}>
          Add User
        </Button>
      </form>
      
    </Card>,
    showPopup && (
        <ErrorModal
          message={error}
          onClose={handleClosePopup}
          key='g2'
        />
      )
    ]
  );
}

export default AddUser;
